const Home = () => {
  const [movies, setmovies] = useState([]);
  const [page, setPage] = useState(1)
  const [search, setSearch] = useState("");


  useEffect(() => {

    let url = `https://api.themoviedb.org/3/movie/popular?page=${page}&api_key=55dd39a670ba91221bdb0502323f7efc`

    if (search) {
      url = `https://api.themoviedb.org/3/search/movie?query=${search}&api_key=55dd39a670ba91221bdb0502323f7efc`
    }


    fetch(url)
      .then((response) => response.json())
      .then((data) => setmovies(data.results));
  }, [page, search]);
  return (
    <div className='p-4 pt-16'>
      <input type="text" placeholder='Search movies...' className="p-2 w-3/4 md:w-1/2 border border-gray-700 rounded-md 
                   bg-gray-900 z-10 bg-opacity-60 backdrop-blur-md text-white 
                   fixed top-16 left-1/2 transform -translate-x-1/2" onChange={(e) => setSearch(e.target.value)} />

      <div className="movies-container grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4 mt-16">
        {movies.map(movie => {
          return (
            <Moviecard key={movie.id}
              movie={movie} />
          )
        })}

      </div>

      <div className="pagination-container flex justify-between rounded mt-5 ">
        <button className=' bg-gray-700 text-white p-2 rounded' onClick={() => setPage((prev) => prev - 1)} disabled={page == 1}>PREV</button>
        <button className=' bg-gray-700 text-white p-2 rounded' onClick={() => setPage((prev) => prev + 1)}>NEXT</button>
      </div>
    </div>

  )
}

export default Home